allUsers.map:
function (doc) {
  if (doc.xcollection=='user') {
    emit(doc._id, { fullName:doc.fullName , emailAddress: doc.emailAddress, userType:doc.userType, permission:doc.permission, organisation:doc.organisation});
  }
}

results.map:
function (doc) {
  if (doc.xcollection == 'studentdenorm') {
    for (var i in doc.results) { 
      for (var j in doc.results[i].paper){
         let subject=doc.results[i].subject;
         let paper=doc.results[i].paper[j].paper;
         let score=doc.results[i].paper[j].score;
         emit(subject, {
           student: doc._id,
           paper: paper,
           score: score,
        });
      }
    }
  }
}

