productSales.map:
function (doc) {
    if (doc.xcollection == 'salesorders') {
      let customer=doc.customer;
      let date=doc.date;
    for (var i in doc.orderlines) { 
         let product=doc.orderlines[i].product;
         let units=doc.orderlines[i].units;
         let price=doc.orderlines[i].price;
         let total=units*price;
         emit([product,date] ,{
          customer: customer,
           units: units,
           price: price,
           total:total
        });
     }
  }
}

