stringify.map:
function (doc) {
    let stringify=doc._id
   switch(doc.xcollection) {
    case "user":
      stringify = doc.fullName;
      break;
    case "product":
      stringify = doc.name;
     break;
  }
  emit(doc._id, {stringify: stringify});
}

