CREATE TABLE `webpagesnosql` (
  `id` int unsigned NOT NULL AUTO_INCREMENT,
  `createdAt` bigint DEFAULT NULL,
  `updatedAt` bigint DEFAULT NULL,
  `updatedBy` int DEFAULT NULL,
  `title` varchar(255) DEFAULT NULL,
  `pagetype` varchar(255) DEFAULT NULL,
  `slug` varchar(255) DEFAULT NULL,
  `embargo` varchar(255) DEFAULT NULL,
  `expires` varchar(255) DEFAULT NULL,
  `parent` int DEFAULT NULL,
  `onMenu` int DEFAULT NULL,
  `status` varchar(255) DEFAULT NULL,
  `headline` varchar(255) DEFAULT NULL,
  `author` int DEFAULT NULL,
  `targetPage` int DEFAULT NULL,
  `targetUrl` varchar(255) DEFAULT NULL,
  `openIn` varchar(255) DEFAULT NULL,
  `pageContent` varchar(255) DEFAULT NULL,
  `view` varchar(255) DEFAULT NULL,
  `image1` varchar(255) DEFAULT NULL,
  `image2` varchar(255) DEFAULT NULL,
